#include <iostream>
#include <algorithm>
#include <map>

typedef unsigned int uint;

struct Problem {
	static std::map<const Problem, uint> memo;
	
	uint k, n;
	
	Problem():
		k(0),
		n(0) {
	}
	
	Problem(uint k, uint n):
		k(k),
		n(n) {
	}
	
	Problem left(const uint choice) const {
		return { this->k, choice };
	}
	
	Problem right(const uint choice) const {
		return { this->k + choice + 1, this->n - choice - 1 };
	}
	
	bool operator< (const Problem& that) const {
		return this->k == that.k ? this->n < that.n : this->k < that.k;
	}
	
	uint cost() const {
		if (0 < memo.count(*this))
			return memo[*this];
		
		if (this->n == 0)
			return memo[*this] = 0;
		
		if (this->n == 1)
			return memo[*this] = 0;
		
		const uint range = 500;
		
		uint best_cost;
		uint choice_min = this->n < range * 2 ? 0       : (this->n / 2 - range);
		uint choice_max = this->n < range * 2 ? this->n : (this->n / 2 + range);
		for (uint choice = choice_min; choice < choice_max; choice++)
		{
			const uint cost = this->left(choice).cost() + this->n * (choice + this->k + 1) + this->right(choice).cost();
			if (choice == 0 || best_cost > cost)
				best_cost = cost;
		}
		
		return memo[*this] = best_cost;
	}
};

std::map<const Problem, uint> Problem::memo;

inline void m(uint k, uint n, uint cost) {
	Problem::memo[Problem(k, n)] = cost;
}

